import { useEffect, useState } from "react";


const useFlip = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  const flipCard = () => {
    setIsFlipped(isFlipped => !isFlipped);
  };

  return [isFlipped, flipCard];
  };




export default useFlip;
